import React,{useState} from 'react';
import {View, Button, Text, StyleSheet} from 'react-native';

const About = () => {
    return (
        <View>
            <Text style={styles.titleText}>About the App</Text>
            <Text> This app is designed for us ADHD folk, who can't get through everyday life without a myriad of alarms on our phones.</Text>
            <Text style={styles.titleText}>About the Creators</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    titleText: {
      fontSize: 50,
      fontWeight: 'bold',
      textAlign: 'center',
    },
    paragraphText: {
        fontSize: 30,
        fontWeight: 'normal',
        textAlign: 'center',
    },
      buttonRow: {
      flexDirection: 'row', // Set the direction to row
      justifyContent: 'space-between', // Align items horizontally
    },
  })

export default About;
