import React, {useState} from 'react';
import {StyleSheet, View, Text, Button, TextInput, FlatList} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

import ButtonSmall from "./ButtonSmall";

const AlarmTest = ({num,color}) => {

  const [hour,setHour] = useState(0)
  const [minute,setMinute] = useState(0)
  const [alarmTitle,setTitle] = useState("")
  const [alarmDesc,setDesc] = useState("")
  const [pomodoros,setPomodoros]= useState([])


    return (
      <View style={{flex: .7, 
                    justifyContent: 'space-evenly',
                    alignItems: 'center',
                    margin:20,
                    padding:20,
                    backgroundColor: color,
                    flexDirection: "row"}} >
                      <Text style={{fontSize: 70, flexDirection: "row", justifyContent: "center", padding:10, color:"black"}}>
                            Alarm
                          </Text>
                      <View style={{flexDirection:"column"}}>
                        <View style={{flexDirection:"row"}}>
                          <ButtonSmall style={styles.button2}
                            title="+1 Hour"
                            onPress = {() => {
                              if (hour == 23) {
                                setHour(0)
                              } else {
                                setHour(hour+1)
                              }
                            }}
                          />
                          <ButtonSmall style={styles.button}
                            title="-1 Hour"
                            onPress = {() => {
                              if (hour == 0) {
                                setHour(23)
                              } else {
                                setHour(hour-1)
                              }
                            }}
                          />
                          </View>
                          <View style={{flexDirection:"row"}}>
                          <ButtonSmall style={styles.button}
                            title="+5 Minute"
                            onPress = {() => {
                              if (minute == 55) {
                                setMinute(0)
                              } else {
                                setMinute(minute+5)
                              }
                            }}
                          />
                          <ButtonSmall style={styles.button}
                            title="-5 Minute"
                            onPress = {() => {
                              if (minute == 0) {
                                setMinute(55)
                              } else {
                                setMinute(minute-5)
                              }
                            }}
                          />
                          </View>

                      </View>
                      <Text style={{fontSize: 20, flexDirection: "row", justifyContent: "center", padding:10, color:"black"}}>
                            {hour} : {minute}
                          </Text>
                      <TextInput 
                            style={{fontSize:20, height: 50, width: 100, borderColor: "#e2f4ad", borderRadius: 10, borderWidth: 5, backgroundColor: "white"}}
                            placeholder="Title"
                            onChangeText={text => {
                              setTitle(text);
                            }}
                            value = {alarmTitle}/>
                      <TextInput 
                            style={{fontSize:20, height: 50, width: 200, borderColor: "#e2f4ad", borderRadius: 10, borderWidth: 5, backgroundColor: "white"}}
                            placeholder="Description"
                            onChangeText={text => {
                              setDesc(text);
                            }}
                            value = {alarmDesc}/>
                      <ButtonSmall style={styles.button}
                            title="ADD"
                            onPress = {() => {}}
                          />
      </View>
    )
  }

const greeting = () => {console.log("Hello World!")}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 8,
    backgroundColor: 'aliceblue',
  },
  box: {
    width: 50,
    height: 50,
  },
  row: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  },
  button: {
    paddingHorizontal: 8,
    paddingVertical: 6,
    borderRadius: 4,
    backgroundColor: 'red',
    alignSelf: 'flex-start',
    marginHorizontal: '1%',
    marginBottom: 6,
    minWidth: '48%',
    textAlign: 'center',
    color: "red",
  },
  button2: {
    margin: 0,
  },
  selected: {
    backgroundColor: 'coral',
    borderWidth: 0,
  },
  buttonLabel: {
    fontSize: 12,
    fontWeight: '500',
    color: 'coral',
  },
  selectedLabel: {
    color: 'white',
  },
  label: {
    textAlign: 'center',
    marginBottom: 10,
    fontSize: 24,
  },
});

export default AlarmTest;
